INSERT INTO `sys_email_templates` VALUES (1,'t_Activation','Profile status was changed to Active','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Dear <RealName></b>,</p>\r\n\r\n<p>Your profile was reviewed and activated !</p>\r\n\r\n<p>Simply follow the link below to enjoy our services:<br /><a href=\"<Domain>member.php\"><Domain>member.php</a></p>\r\n\r\n<p>Your identification number (ID): <span style=\"color:#FF6633\"><recipientID></span></p>\r\n\r\n<p>Your e-mail used for registration: <span style=\"color:#FF6633\"><Email></span></p>\r\n\r\n<p><b>Thank you for using our services!</b></p>\r\n\r\n<p>--</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\n<br />Auto-generated e-mail, please, do not reply!!!</p></body></html>','Profile activation message template.',0);
INSERT INTO `sys_email_templates` VALUES (3,'t_AdminEmail','Message from <SiteName> Admin: <MessageSubject>','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Dear <RealName></b>,</p>\r\n\r\n<p>Administration of the <a href=\"<Domain>\"><SiteName></a> is glad to inform you that </p>\r\n\r\n<p>=========================</p>\r\n<p style=\"color:#3B5C8E\"><MessageText></p>\r\n<p>=========================</p>\r\n\r\n\r\n <p style=\"font-size:10px;\">NOTE: You received this message because our records show that you are a registered member of <a href=\"<Domain>\"><SiteName></a> or subscribed to the delivery.\r\n If you wish to unregister, log in to your member account and hit \"Unregister\".</p>\r\n\r\n<p>-----</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\nAuto-generated e-mail, please, do not reply!!!</p></body></html>','Email template for message sending from the Admin Panel.',0);
INSERT INTO `sys_email_templates` VALUES (5,'t_Compose','Notification about new messages in the inbox','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Hello <RealName></b>,</p>\r\n\r\n<p>You have received a message from <a href=\"<ProfileUrl>\"><ProfileReference></a>!</p>\r\n\r\n<p>To check this message login to your account here: <a href=\"<Domain>member.php\"><Domain>member.php</a></p>\r\n\r\n<p>---</p>\r\nBest regards,  <SiteName> \r\n<p style=\"font: bold 10px Verdana; color:red\">!!!Auto-generated e-mail, please, do not reply!!!</p></body></html>','Email template for notification about new messages in the inbox.',0);
INSERT INTO `sys_email_templates` VALUES (7,'t_Confirmation','Confirm your profile','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Dear <RealName></b>,</p>\r\n\r\n<p>Thank you for registering at <SiteName>!</p>\r\n\r\n<p style=\"color:#3B5C8E\">CONFIRMATION CODE: <ConfCode></p>\r\n\r\n<p>Or you can also simply follow the link below:\r\n<a href=\"<ConfirmationLink>\"><ConfirmationLink></a></p>\r\n\r\n<p>This is necessary to complete your registration.<br />Without doing that you won\'t be submitted to our database.</p>\r\n\r\n<p>Your identification number (ID): <span style=\"color:#FF6633; font-weight:bold;\"><recipientID></span></p>\r\n\r\n<p>Your e-mail used for registration: \r\n<span style=\"color:#FF6633\"><Email></span></p>\r\n\r\n<p><b>Thank you for using our services!</b></p>\r\n\r\n<p>--</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\n<br />Auto-generated e-mail, please, do not reply!!!</p></body></html>','Profile e-mail confirmation message template.',0);
INSERT INTO `sys_email_templates` VALUES (9,'t_CupidMail','Match Notification','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Hello <RealName></b>,</p>\r\n\r\n<p>We are glad to inform you that a profile was added or modified at <Domain> that matches yours.</p>\r\n\r\n<p>Match profile:<span style=\"color:#FF6633\"><a href=\"<MatchProfileLink>\"><MatchProfileLink></a></span></p>\r\n\r\n<p>Your Member ID:<span style=\"color:#FF6633\"><StrID></span></p>\r\n\r\n<p>--</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\n<br />Auto-generated e-mail, please, do not reply!!!</p></body></html>','Cupid mail template',0);
INSERT INTO `sys_email_templates` VALUES (11,'t_Forgot','Forgot password email message','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Dear <RealName></b>,</p>\r\n\r\n<p>Your member ID: <span style=\"color:#FF6633\"><recipientID></span></p>\r\n\r\n<p>Your password: <span style=\"color:#FF6633\"><Password></span></p>\r\n\r\n<p>You must login here: <span style=\"color:#FF6633\"><a href=\"<Domain>member.php\"><Domain>member.php</a></span></p>\r\n\r\n<p><b>Thank you for using our services!</b></p>\r\n\r\n<p>--</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\n<br />Auto-generated e-mail, please, do not reply!!!</p></body></html>','Forgot password email message',0);
INSERT INTO `sys_email_templates` VALUES (13,'t_FreeEmail','Free contact information','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Dear <RealName></b>,</p>\r\n\r\n<p>You have requested <strong><profileNickName></strong>\'s contact information.</p>\r\n\r\n<p><profileContactInfo></p>\r\n\r\n<p>View member\'s profile: <a href=\"<Domain>profile.php?ID=<profileID>\"><Domain>profile.php?ID=<profileID></a></p>\r\n\r\n<p><b>Thank you for using our services!</b></p>\r\n\r\n<p>--</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\n<br />Auto-generated e-mail, please, do not reply!!!</p></body></html>','Free contact information letter template sent to members requesting contact information.',0);
INSERT INTO `sys_email_templates` VALUES (15,'t_MemExpiration','<your subject here>','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Hello <RealName></b>,</p>\r\n\r\n<p>We are notifying you that your <SiteName> <MembershipName> will expire in <ExpireDays> days (-1 = already expired).\r\n\r\n To renew your membership login to your <SiteName> account at <a href=\"<Domain>member.php\"><Domain>member.php</a> and go to <a href=\"<Domain>modules/?r=membership/index/\">My Membership</a></p>\r\n\r\n<p>Your Member ID: <span style=\"color:#FF6633; font-weight:bold;\"><recipientID></span></p>\r\n\r\n<p>--</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\n<br />Auto-generated e-mail, please, do not reply!!!</p></body></html>','Membership expiration letter sent to members whose membership level expires.',0);
INSERT INTO `sys_email_templates` VALUES (16,'t_MemChanged','Your membership level was changed','<html><head></head><body style=\"font: 12px Verdana; color:#000000\">\r\n<p><b>Hello <RealName></b>,</p>\r\n\r\n<p>Your membership level was changed to: <b><MembershipLevel></b></p>\r\n\r\n<p>Please refer to membership page for more details: <a href=\"<Domain>modules/?r=membership/index/\">My Membership</a></p>\r\n\r\n<p>--</p>\r\n<p style=\"font: bold 10px Verdana; color:red\"><SiteName> mail delivery system!!!\r\n<br />Auto-generated e-mail, please, do not reply!!!</p></body></html>','The letter is sent to members whose membership level was changed.',0);
